    private void btRegistrarRecetaActionPerformed(java.awt.event.ActionEvent evt) {//GEN-FIRST:event_btRegistrarRecetaActionPerformed
        Receta rec = new Receta();
        EReceta rc = new EReceta();
        
        if ( this.txtInstruccion.getText().length() == 0){
            JOptionPane.showMessageDialog(rootPane, "Ingrese las instrucciones de la receta");
            return;
        }
        if ( this.txtCantidad.getText().length() == 0){
            JOptionPane.showMessageDialog(rootPane, "Ingrese la cantidad");
            return;
        }
        
        int cantidad = Integer.parseInt(this.txtCantidad.getText());
        rc.setCantidad(cantidad);
        rc.setInstrucciones(this.txtInstruccion.getText());
        
        try {
            rec.registrarReceta(rc);
        } catch (SQLException ex) {
            Logger.getLogger(NuevoMedicamento.class.getName()).log(Level.SEVERE, null, ex);
        }
    }//GEN-LAST:event_btRegistrarRecetaActionPerformed
        
